languages.basicsCode:
// Output text
Console.WriteLine("Hello World");

// To input number variables we use int, double, and float.
// Integers work only with whole numbers.
int age = 20;
// Double and float work for numbers with decimals.
double price = 15.5;
string name = "Ali";
bool isStudent = true;

// Arithmetic
int sum = age + 5;
Console.WriteLine(sum); // output = 25

// Comments
// This is a single line comment
/*
  This is a multi-line comment.
  It can span multiple lines
  and include paragraphs of explanation.
*/

languages.controlFlowCode:
// If-else statement
if(age >= 18)
{
    Console.WriteLine("Adult");
}
else
{
    Console.WriteLine("Minor");
}

// Loop - for
for(int i = 0; i < 5; i++)
{
    Console.WriteLine("Count: " + i);
}

// Loop - while
int j = 0;
while(j < 3)
{
    Console.WriteLine("While loop: " + j);
    j++;
}

// Function
int AddNumbers(int a, int b)
{
    return a + b;
}

Console.WriteLine("Sum: " + AddNumbers(5, 10));

